// This is where you'll paste the JSON data from your Streamlit export
const data = {
  company_data: {
    company_name: "Example Corp",
    head_name: "John Doe",
    head_email: "john@example.com",
    website: "https://example.com",
    linkedin_url: "https://linkedin.com/in/johndoe"
  },
  cold_email: "Your generated email content here",
  timestamp: new Date().toISOString()
};

// Prepare email data
const emailData = {
  to: data.company_data.head_email,
  subject: `Reaching out to ${data.company_data.company_name}`,
  body: data.cold_email,
  company: data.company_data.company_name,
  timestamp: data.timestamp
};

return {json: emailData};
